mapper.select:
SELECT BBS_NO, TOPIC, USER_EMAIL, TITLE, CONTENT, WDATE, GOOD, READCOUNT, IMAGEURL, DEL FROM ( SELECT ROW_NUMBER() OVER (ORDER BY BBS_NO DESC) as rnum, BBS_NO, TOPIC, USER_EMAIL, TITLE, CONTENT, WDATE, GOOD, READCOUNT, IMAGEURL, DEL FROM FOOT_BBS WHERE 1 = 1
AND TITLE like '%'||#{s_keyword}||'%' AND CONTENT like '%'||#{s_keyword}||'%'
) A WHERE rnum between ${start} AND ${end}

SELECT BBS_NO, TOPIC, USER_EMAIL, TITLE, CONTENT, WDATE, GOOD, READCOUNT, IMAGEURL, DEL
FROM FOOT_BBS
WHERE bbs_no=#{bbs_no}

mapper.update:
UPDATE FOOT_BBS SET
READCOUNT =READCOUNT+1
WHERE bbs_no=#{bbs_no}

UPDATE FOOT_BBS SET 
TITLE=#{title}, CONTENT=#{content}, WDATE=SYSDATE 
WHERE bbs_no=#{bbs_no}

UPDATE FOOT_BBS SET 
DEL=#{del} 
WHERE bbs_no=#{bbs_no}
