mapper.update:
UPDATE FOOT_BBS_COMMENT SET
CONTENT = #{content}, WDATE = SYSDATE
WHERE COMMENT_NO=#{comment_no}

   	
 		UPDATE FOOT_BBS SET
		COMMENTCOUNT = COMMENTCOUNT+1
		WHERE bbs_no=#{bbs_no}

   	
 		UPDATE FOOT_BBS SET
		COMMENTCOUNT = COMMENTCOUNT-1
		WHERE bbs_no=#{bbs_no}

mapper.select:
SELECT BBS_NO, TOPIC, USER_EMAIL, TITLE, CONTENT, WDATE, GOOD, READCOUNT, IMAGEURL, DEL, COMMENTCOUNT FROM ( SELECT ROW_NUMBER() OVER (ORDER BY BBS_NO DESC) as rnum, BBS_NO, TOPIC, USER_EMAIL, TITLE, CONTENT, WDATE, GOOD, READCOUNT, IMAGEURL, DEL, COMMENTCOUNT FROM FOOT_BBS WHERE 1 = 1
AND TITLE like '%'||#{s_keyword}||'%' AND CONTENT like '%'||#{s_keyword}||'%'
) A WHERE rnum between ${start} AND ${end}

mapper.update:
UPDATE FOOT_BBS SET
READCOUNT = READCOUNT+1
WHERE bbs_no=#{bbs_no}

UPDATE FOOT_BBS SET
GOOD = GOOD+1
WHERE BBS_NO=#{bbs_no}

mapper.delete:
DELETE FROM FOOT_BBS_LIKE
WHERE PARENT_BBS_NO = #{parent_bbs_no}
AND USER_EMAIL = #{user_email}

mapper.update:
UPDATE FOOT_BBS SET
GOOD = GOOD-1
WHERE BBS_NO=#{bbs_no}

mapper.select:
SELECT *
FROM FOOT_BBS_LIKE
WHERE PARENT_BBS_NO=#{parent_bbs_no}
AND USER_EMAIL=#{user_email}

mapper.update:
UPDATE FOOT_BBS SET 
TITLE=#{title}, CONTENT=#{content}, WDATE=SYSDATE 
WHERE bbs_no=#{bbs_no}

UPDATE FOOT_BBS SET 
DEL=#{del} 
WHERE bbs_no=#{bbs_no}
